@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    
    HIChartView *chartView = [[HIChartView alloc] initWithFrame:self.view.bounds];

    chartView.theme = @"sand-signika";
    
    HIOptions *options = [[HIOptions alloc]init];
    
    HIData *data = [[HIData alloc] init];
    data.csv = [self csv];
    
    HIChart *chart = [[HIChart alloc]init];
    chart.type = @"heatmap";
    chart.margin = @[@60, @10, @80, @50];
    
    HIBoost *boost = [[HIBoost alloc] init];
    boost.useGPUTranslations = [[NSNumber alloc] initWithBool:true];
    
    HITitle *title = [[HITitle alloc]init];
    title.text = @"Highcharts heat map";
    title.align = @"left";
    title.x = @40;
    
    HISubtitle *subtitle = [[HISubtitle alloc]init];
    subtitle.text = @"Temperature variation by day and hour through 2013";
    subtitle.align = @"left";
    subtitle.x = @40;
    
    HIXAxis *xaxis = [[HIXAxis alloc]init];
    xaxis.type = @"datetime";
    xaxis.min = @1356998400000;
    xaxis.max = @1388534400000;
    xaxis.labels = [[HILabels alloc] init];
    xaxis.labels.align = @"left";
    xaxis.labels.x = @5;
    xaxis.labels.y = @14;
    xaxis.showLastLabel = [[NSNumber alloc] initWithBool:false];
    xaxis.tickLength = @16;
    
    HIYAxis *yaxis = [[HIYAxis alloc] init];
    yaxis.title = [[HITitle alloc] init];
    yaxis.labels = [[HILabels alloc] init];
    yaxis.labels.format = @"{value}:00";
    yaxis.minPadding = @0;
    yaxis.maxPadding = @0;
    yaxis.startOnTick = [[NSNumber alloc] initWithBool:false];
    yaxis.endOnTick = [[NSNumber alloc] initWithBool:false];
    yaxis.tickPosition = @"[0, 6, 12, 18, 24]";
    yaxis.tickWidth = @1;
    yaxis.min = @0;
    yaxis.max = @23;
    yaxis.reversed = [[NSNumber alloc] initWithBool:true];
    
    HIColorAxis *coloraxis = [[HIColorAxis alloc] init];
    coloraxis.stops = @[@[@0, @"#3060cf"], @[@0.5, @"'#fffbbc'"], @[@0.9, @"#c4463a"], @[@1, @"#c4463a"]];
    coloraxis.min = @-15;
    coloraxis.max = @25;
    coloraxis.startOnTick = [[NSNumber alloc] initWithBool:false];
    coloraxis.endOnTick = [[NSNumber alloc] initWithBool:false];
    coloraxis.labels = [[HILabels alloc] init];
    coloraxis.labels.format = @"{value}℃";
    
    HIHeatmap *heatmap = [[HIHeatmap alloc] init];
    heatmap.boostThreshold = @100;
    heatmap.borderWidth = @0;
    heatmap.nullColor = [[HIColor alloc] initWithHexValue:@"EFEFEF"];
    heatmap.colsize = @(24 * 36e5);
    heatmap.tooltip = [[HITooltip alloc] init];
    heatmap.tooltip.headerFormat = @"Temperature<br/>";
    heatmap.tooltip.pointFormat =@"{point.x:%e %b, %Y} {point.y}:00: <b>{point.value} ℃</b>";
    heatmap.turboThreshold = @1.7976931348623157e+308;
    
    options.data = data;
    options.chart = chart;
    options.boost = boost;
    options.title = title;
    options.subtitle = subtitle;
    options.xAxis = [NSMutableArray arrayWithObjects:xaxis, nil];
    options.yAxis = [NSMutableArray arrayWithObjects:yaxis, nil];
    options.colorAxis = coloraxis;
    options.series = [NSMutableArray arrayWithObjects:heatmap, nil];
    
    chartView.options = options;
    
    [self.view addSubview:chartView];
}

