#import <UIKit/UIKit.h>
#import <Highcharts/Highcharts.h>

@interface ViewController ()

@end

@implementation ViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    
    HIChartView *chartView = [[HIChartView alloc] initWithFrame:self.view.bounds];
    
    HIOptions *options = [[HIOptions alloc] init];
    
    HIChart *chart = [[HIChart alloc] init];
    chart.type = @"line";
    chart.zoomType = @"x";
    
    HIBoost *boost = [[HIBoost alloc] init];
    boost.useGPUTranslations = [[NSNumber alloc] initWithBool:true];

    HITitle *title = [[HITitle alloc] init];
    title.text = @"Highcharts drawing 500 points";
    
    HISubtitle *subtitle = [[HISubtitle alloc] init];
    subtitle.text = @"Using the Boost module";
    
    HITooltip *tooltip = [[HITooltip alloc] init];
    tooltip.valueDecimals = @2;
    
    HILine *line = [[HILine alloc] init];
    line.data = [self data];
    line.lineWidth = @0.5;
    
    options.chart = chart;
    options.boost = boost;
    options.title = title;
    options.subtitle = subtitle;
    options.tooltip = tooltip;
    options.series = [NSMutableArray arrayWithObjects:line, nil];
    
    chartView.options = options;
    
    [self.view addSubview:chartView];
}

